import { withDebugTracing } from "@angular/router";
import { Piece, PieceColor } from "../../piece";


export abstract class Rook extends Piece  {

    possibleMoves(colorBoard: PieceColor[][]): number[][] {
        var possibleMoves = []
        var boardWidth = colorBoard[0].length;
        var boardHeight = colorBoard.length;

        //right
        for (let i = this.fieldWidth + 1; i < boardWidth; i++) {
            var color = colorBoard[this.fieldHeight][i];
            if (color == this.color) { break }
            else if (color == PieceColor.None) { possibleMoves.push([this.fieldHeight, i]) }
            else {
                possibleMoves.push([this.fieldHeight, i]);
                break;
            }
        }
        //left
        for (let i = this.fieldWidth - 1; i >= 0; i--) {
            var color = colorBoard[this.fieldHeight][i];
            if (color == this.color) { break }
            else if (color == PieceColor.None) { possibleMoves.push([this.fieldHeight, i]) }
            else {
                possibleMoves.push([this.fieldHeight, i]);
                break;
            }
        }
        //up
        for (let i = this.fieldHeight - 1; i >= 0; i--) {
            var color = colorBoard[i][this.fieldWidth];
            if (color == this.color) { break }
            else if (color == PieceColor.None) { possibleMoves.push([i, this.fieldWidth]) }
            else {
                possibleMoves.push([i, this.fieldWidth]);
                break;
            }
        }
        //down
        for (let i = this.fieldHeight + 1; i < boardHeight; i++) {
            var color = colorBoard[i][this.fieldWidth];
            if (color == this.color) { break }
            else if (color == PieceColor.None) { possibleMoves.push([i, this.fieldWidth]) }
            else {
                possibleMoves.push([i, this.fieldWidth]);
                break;
            }
        }

        return possibleMoves;
    }


    constructor(fieldHeight: number, fieldWidth: number) {
        super(fieldHeight, fieldWidth);
    }
}
