import { Field } from "../field/field";
import { BlackBishop } from "../pieces/piece/blackBishop/black-bishop";
import { BlackKing } from "../pieces/piece/blackKing/black-king";
import { BlackKnight } from "../pieces/piece/blackKnight/black-knight";
import { BlackPawn } from "../pieces/piece/blackPawn/black-pawn";
import { BlackQueen } from "../pieces/piece/blackQueen/black-queen";
import { BlackRook } from "../pieces/piece/blackRook/black-rook";
import { WhitePawn } from "../pieces/piece/whitePawn/white-pawn";

export class Board {
    fields: Field[][] = [];
    width: number = 0;
    height: number = 0;

    setBaseBoard() {
        this.height = 8;
        this.width = 8;
        var nextSquareWhite = true;
        for (let i = 0; i < this.width; i++) {
            this.fields.push([]);
            for (let j = 0; j < this.height; j++) {
                this.fields[i].push(new Field(nextSquareWhite));
                nextSquareWhite = !nextSquareWhite;
            }
            if (this.width % 2 === 0) {
                nextSquareWhite = !nextSquareWhite;
            }
        }
    }

    setBasePosition() {
        this.fields[0][0].setPiece(new BlackRook());
        this.fields[0][1].setPiece(new BlackKnight());
        this.fields[0][2].setPiece(new BlackBishop());
        this.fields[0][3].setPiece(new BlackQueen());
        this.fields[0][4].setPiece(new BlackKing());
        this.fields[0][5].setPiece(new BlackBishop());
        this.fields[0][6].setPiece(new BlackKnight());
        this.fields[0][7].setPiece(new BlackRook());
        for (let i = 0; i < this.width; i++) { this.fields[1][i].setPiece(new BlackPawn()); }

        for (let i = 0; i < this.width; i++) { this.fields[6][i].setPiece(new WhitePawn()); }

        this.fields[7][0].setPiece(new WhiteRook());
        this.fields[7][1].setPiece(new BlackRook());
        this.fields[7][2].setPiece(new BlackRook());
        this.fields[7][3].setPiece(new BlackRook());
        this.fields[7][4].setPiece(new BlackRook());
        this.fields[7][5].setPiece(new BlackRook());
        this.fields[7][6].setPiece(new BlackRook());
        this.fields[7][7].setPiece(new BlackRook());


    }

    constructor() {
        this.setBaseBoard();
        this.setBasePosition();
    }
}
