import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-square',
  template: `
    <button>
      <!-- <img src="assets/black_king.svg" alt="Ikona" /> -->
      {{value}}
    </button>
  `,
    styles: [
    `
      .square {
        width: 50px;
        height: 0;
        padding-bottom: 50px; /* Ustalamy wysokość w stosunku do szerokości, aby uzyskać kwadrat */
        border: 1px solid #ccc;
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 20px;
      }
    `,
  ],
})
export class SquareComponent  {

  @Input() value: number = 0;

}
