import { Field } from "../field/field";
import { Piece, PieceColor } from "../pieces/piece";
import { BlackKnight } from "../pieces/piece/knight/blackKnight/black-knight";
import { WhiteKnight } from "../pieces/piece/knight/whiteKnight/white-knight";


export class Board {
    fields: Field[][] = [];
    width: number = 0;
    height: number = 0;
    anyButtonClicked: boolean = false;
    markedField: any = null;
    colorMap: any = null;


    buttonClicked(height: number, width: number) : void {
        this.checkFieldArgs(height, width);
        if (this.anyButtonClicked) {
            this.secondButtonClicked(height, width)
        }
        else {
            this.firstButtonClicked(height, width)
        }
    }

    markPossibleMoves(possibleMoves: number[][], pieceColor : PieceColor) { 
        for (let i = 0; i < possibleMoves.length; i++) {

            var y = possibleMoves[i][0];
            var x = possibleMoves[i][1];
            var field = this.fields[y][x];

            //if no piece
            if (field.piece == null) { field.markedToMove = true;}
            //if piece same color
            else if (field.piece.color == pieceColor) { }
            else { field.markedToCapture == true;}

            }
    }


    firstButtonClicked(height: number, width: number) : void {
        //mark
        if (this.fields[height][width].piece) {
            var possibleMoves = this.fields[height][width].piece.possibleMoves(this.colorMap);

            this.markedField = this.fields[height][width];
            this.fields[height][width].markedToMove = true;
            this.anyButtonClicked = true;
        }
    }

    secondButtonClicked(height: number, width: number) : void {
        //to do podzial na biale i czarne
        if (!this.fields[height][width].marked() && this.fields[height][width].piece == null) { }
        else if (this.fields[height][width].markedToMove) {
            //to do ruch
            // this.move()
            this.unmarkButtons();
        }
        else if (this.fields[height][width].markedToCapture) {
            //to do bicie albo ruch
            // this.capture()
            this.unmarkButtons();
        }
        else if (!this.fields[height][width].marked()) {
            this.unmarkButtons();
            this.fields[height][width].markedToMove = true;
            this.anyButtonClicked = true;
        }
    }

    checkFieldArgs(height: number, width: number) : void {
        if (!Number.isInteger(height) || height < 0 || height >= this.height) {
            console.error(" height: ${height} is invalid")
        }
        if (!Number.isInteger(width) || width < 0 && width >= this.width) {
            console.error("width: ${width} is invalid")
        }
    }

    setBaseBoard() : void {
        this.height = 8;
        this.width = 8;
        var nextSquareWhite = true;
        for (let i = 0; i < this.width; i++) {
            this.fields.push([]);
            for (let j = 0; j < this.height; j++) {
                var field = new Field(nextSquareWhite, i, j);
                field.board = this;
                this.fields[i].push(field);
                nextSquareWhite = !nextSquareWhite;
            }
            if (this.width % 2 === 0) {
                nextSquareWhite = !nextSquareWhite;
            }
        }
    }

    unmarkButtons() : void {
        for (let i = 0; i < this.width; i++) {
            for (let j = 0; j < this.height; j++) {
                this.fields[i][j].unmark();
                this.anyButtonClicked = false;
            }
        }
    }

    setBasePosition() {
        // this.fields[0][0].setPiece(new BlackRook());
        this.fields[0][1].setPiece(new BlackKnight(0, 1));
        // this.fields[0][2].setPiece(new BlackBishop());
        // this.fields[0][3].setPiece(new BlackQueen());
        // this.fields[0][4].setPiece(new BlackKing());
        // this.fields[0][5].setPiece(new BlackBishop());
        // this.fields[0][6].setPiece(new BlackKnight());
        // this.fields[0][7].setPiece(new BlackRook());
        // for (let i = 0; i < this.width; i++) { this.fields[1][i].setPiece(new BlackPawn()); }

        // for (let i = 0; i < this.width; i++) { this.fields[6][i].setPiece(new WhitePawn()); }

        // this.fields[7][0].setPiece(new WhiteRook());
        this.fields[7][1].setPiece(new WhiteKnight(7, 1));
        // this.fields[7][2].setPiece(new WhiteBishop());
        // this.fields[7][3].setPiece(new WhiteQueen());
        // this.fields[7][4].setPiece(new Whiteking());
        // this.fields[7][5].setPiece(new WhiteBishop());
        // this.fields[7][6].setPiece(new WhiteKnight());
        // this.fields[7][7].setPiece(new WhiteRook());


    }

    generateColorMap(): PieceColor[][] {
        var colorMap = new Array(this.height).fill([]);
        for (let i = 0; i < this.width; i++) {
            colorMap.push([])
            for (let j = 0; j < this.height; j++) {
                if (this.fields[i][j].piece) {
                    var color: PieceColor;
                    color = this.fields[i][j].piece.color;
                    colorMap[i].push(color);
                }
                else {
                    colorMap[i].push(PieceColor.None);
                }
            }
        }
        return colorMap;
    }

    constructor() {
        this.setBaseBoard();
        this.setBasePosition();
        this.colorMap = this.generateColorMap();
    }
}
