export enum PieceColor {
    White = "white",
    Black = "black",
    None = "none"
}



export abstract class Piece {
    abstract iconPath: string;
    fieldHeight: number;
    fieldWidth: number;
    color: PieceColor;

    validateField(boardHeight: number, boardWidth: number, fieldRow: number, fieldColumn: number) {
        if (!Number.isInteger(fieldRow) || fieldRow < 0 || fieldRow >= boardHeight) {
            return false;
        }
        if (!Number.isInteger(fieldColumn) || fieldColumn < 0 && fieldColumn >= boardWidth) {
            return false;
        }
        return true;
    }

    abstract possibleMoves(colorBoard: PieceColor[][]): number[][];

    constructor(fieldHeight: number, fieldWidth: number, color: PieceColor) {
        this.fieldHeight = fieldHeight;
        this.fieldWidth = fieldWidth;
        this.color = color;
    }
}
