import { Component, OnInit } from '@angular/core';
import { Field } from 'src/app/chess/field/field';
import { BlackBishop } from '../chess/pieces/piece/blackBishop/black-bishop';
import { BlackKing } from '../chess/pieces/piece/blackKing/black-king';
import { BlackKnight } from '../chess/pieces/piece/blackKnight/black-knight';
import { BlackPawn } from '../chess/pieces/piece/blackPawn/black-pawn';
import { BlackQueen } from '../chess/pieces/piece/blackQueen/black-queen';
import { BlackRook } from '../chess/pieces/piece/blackRook/black-rook';
import { WhiteBishop } from '../chess/pieces/piece/whiteBishop/white-bishop';
import { Whiteking } from '../chess/pieces/piece/whiteKing/whiteking';
import { WhiteKnight } from '../chess/pieces/piece/whiteKnight/white-knight';
import { WhitePawn } from '../chess/pieces/piece/whitePawn/white-pawn';
import { WhiteQueen } from '../chess/pieces/piece/whiteQueen/white-queen';
import { WhiteRook } from '../chess/pieces/piece/whiteRook/white-rook';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css']
})
export class BoardComponent  implements OnInit {
  squares: Field[][];
  width: number = 8;
  height: number = 8;
  nextSquareWhite: boolean = true;
  position: any;


  letterToPiece(letter: string) {

    switch (letter) {
      case "bB":
        return new BlackBishop;
      case "bKi":
        return new BlackKing;
      case "bKn":
        return new BlackKnight;
      case "bP":
        return new BlackPawn;
      case "bQ":
        return new BlackQueen;
      case "bR":
        return new BlackRook;
      case "wB":
        return new WhiteBishop;
      case "wKi":
        return new Whiteking;
      case "wKn":
        return new WhiteKnight;
      case "wP":
        return new WhitePawn;
      case "wQ":
        return new WhiteQueen;
      case "wR":
        return new WhiteRook;
      default:
        return "popraw kod klocu";
    }
  }

  ngOnInit() {
    this.http.get<any>('assets/person.json').subscribe(
      (data) => {
        this.position = data.pos;
        console.log(this.position);
      },
      (error) => {
        console.error('Błąd przy wczytywaniu pliku JSON:', error);
      }
    );
  }

  constructor(private http: HttpClient) {
    this.squares = [];
    this.width = this.position.width;
    this.height = this.jsonData["height"];
    for (let i = 0; i < this.width; i++) {
      this.squares.push([]);
      for (let j = 0; j < this.height; j++) {
        this.squares[i].push(new Field(this.nextSquareWhite, new BlackBishop ));
        this.nextSquareWhite = !this.nextSquareWhite;
      }
      if (this.width % 2 === 0) {
        this.nextSquareWhite = !this.nextSquareWhite;
      }
    }
  }
}
