import { NONE_TYPE } from "@angular/compiler";
import { Piece } from "../pieces/piece";

export class Field {
    colour: string;
    piece: any = null;
    board: any = null;
    width: number = -1;
    height: number = -1;
    markedToMove: boolean = false;
    markedPossibleMove: boolean = false;
    markedToCapture: boolean = false;

    setPiece(newPiece: Piece) {
        this.piece = newPiece;
    }

    unmark() {
        this.markedToMove = false;
        this.markedPossibleMove = false;
        this.markedToCapture = false;
    }

    fieldMarked() {
        console.log('Button clicked from field! Value:', this.colour, this.width, this.height);
        this.board.buttonClicked(this.height, this.width);
    }

    constructor(white: boolean, height: number, width: number) {
        if (white) { this.colour = "burlywood"; }
        else { this.colour = "saddlebrown"; }
        this.width = width;
        this.height = height;
    }
}
