import { Component, OnInit } from '@angular/core';
import { Field } from 'src/app/chess/field/field';

@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css']
})
export class BoardComponent  {
  squares: Field[][];
  width: number = 8;
  height: number = 8;
  nextSquareWhite: boolean = true;

  constructor() {
    this.squares = [];
    for (let i = 0; i < this.width; i++) {
      this.squares.push([]);
      for (let j = 0; j < this.height; j++) {
        this.squares[i].push(new Field(this.nextSquareWhite, ""));
        this.nextSquareWhite = !this.nextSquareWhite;
      }
      if (this.width % 2 === 0) {
        this.nextSquareWhite = !this.nextSquareWhite;
      }
    }
  }
}
