import { NONE_TYPE } from "@angular/compiler";
import { Piece } from "../pieces/piece";

export class Field {
    colour: string;
    piece: any = null;
    board: any = null;

    setPiece(newPiece: Piece) {
        this.piece = newPiece;
    }

    marked() {
        console.log('Button clicked from field! Value:', this.colour);
    }

    constructor(white: boolean) {
        if (white) { this.colour = "burlywood"; }
        else { this.colour = "saddlebrown"; }
    }
}
