import { Field } from "../field/field";
import { BlackRook } from "../pieces/piece/blackRook/black-rook";

export class Board {
    fields: Field[][] = [];
    width: number = 0;
    height: number = 0;

    setBaseBoard() {
        this.height = 8;
        this.width = 8;
        var nextSquareWhite = true;
        for (let i = 0; i < this.width; i++) {
            this.fields.push([]);
            for (let j = 0; j < this.height; j++) {
                this.fields[i].push(new Field(nextSquareWhite));
                nextSquareWhite = !nextSquareWhite;
            }
            if (this.width % 2 === 0) {
                nextSquareWhite = !nextSquareWhite;
            }
        }
    }

    setBasePosition() {
        this.fields[0][0].setPiece(new BlackRook());

    }

    constructor() {
        this.setBaseBoard();
        this.setBasePosition();
    }
}
