import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css']
})
export class BoardComponent  {
  squares: number[][];
  xIsNext: boolean;
  winner: string;
  player: string;

  constructor() {
    this.xIsNext = true;
    this.winner = "";
    this.player = "";
    this.squares = [];
    for (let i = 0; i < 8; i++) {
      this.squares.push([]);
      for (let j = 0; j < 8; j++) {
        this.squares[i].push(1);
      }
    }
  }
}
