import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-square',
  styleUrls: ['./square.component.css'],
  templateUrl: './square.component.html',

})
export class SquareComponent  {

  @Input() value: number = 0;

  onSquareClick() {
    console.log('Button clicked! Value:', this.value);
  }

}
