import { Field } from 'src/app/chess/field/field';
import { BlackBishop } from '../chess/pieces/piece/blackBishop/black-bishop';
import { BlackPawn } from '../chess/pieces/piece/blackPawn/black-pawn';

@Component({
  selector: 'app-square',
  styleUrls: ['./square.component.css'],
  templateUrl: './square.component.html',

})
export class SquareComponent  {


  @Input() field: Field = new Field(true);


  onSquareClick() {
    this.field.marked();
  }

}
